$(document).ready(function (a) {
	a(".ncs-animateblock").on("mouseenter", function () {
		a(".ncs-animateblock .about_client").addClass("animate");
		a(".ncs-animateblock .about_client > h4").addClass("font-60");
		a(".ncs-animateblock .about_client > h1").addClass("font-130")
	});
	a(".ncs-animateblock").on("mouseleave", function () {
		a(".ncs-animateblock .about_client").removeClass("animate");
		a(".ncs-animateblock .about_client > h4").removeClass("font-60");
		a(".ncs-animateblock .about_client > h1").removeClass("font-130")
	});
	a(".casestudy-ncs .solution-block").on("mouseenter", function () {
		a(this).find("p, h2").addClass("text-white")
	});
	a(".casestudy-ncs .solution-block").on("mouseleave", function () {
		a(this).find("p, h2").removeClass("text-white")
	});
	a(".content-slider").lightSlider({
		loop: true,
		keyPress: true
	});
	a("a").attr("tabindex", -1)
});
