function returnFalse() {
	return false
}

function appleImagesTwoAnimation() {
	var a = $(window);
	a.on("scroll", function () {
		var b = $("#ourTeamSection .front-img") || " ";
		var d = a.scrollTop();
		var c = b.offset().top;
		if (d > (c - 430)) {
			b.addClass("rightto63")
		} else {
			b.removeClass("rightto63")
		}
	})
}

function appleImagesFourAnimation() {
	var a = $(window);
	a.on("scroll", function () {
		var b = $("#overflow-slide_1") || " ";
		var c = $(".transforming-imgs .imageforanimate1");
		var d = $(".transforming-imgs .imageforanimate2");
		var e = $(".transforming-imgs .imageforanimate4");
		var g = a.scrollTop();
		var f = b.offset().top;
		if (g > (f - 130)) {
			c.addClass("animation-img1");
			d.addClass("animation-img2");
			e.addClass("animation-img4")
		} else {
			c.removeClass("animation-img1");
			d.removeClass("animation-img2");
			e.removeClass("animation-img4")
		}
	})
}($("#ourTeamSection .front-img").length) ? appleImagesTwoAnimation(): returnFalse();
($("#overflow-slide_1").length) ? appleImagesFourAnimation(): returnFalse();
